public class datatypes {

	/**
	 * @param args
	 */
	public static void main(String[] args) {
		// TODO Auto-generated method stub
		//declare types
		byte a = 1;
		short b = 100;
		int c = 10000;
		long d = 100000;
		float e = 5.755555555f;
		double f = 1.112233445566778899999;
		boolean g = true;
		char h = 'A';
		String i = "two and 2";
		//print types with print method
		System.out.println(a+"\n"+b+"\n"+c+"\n"+d+"\n"+e+"\n"+f+"\n"+g+"\n"+h+"\n"+i);

	}

}
